

SELECT * FROM `category`;

SELECT a.Name,
       start_price,
       image,
       (sum(b.sum)+a.start_price)Последняя_ставка,
       count(b.sum)Кол-во_ставок,
       c.name,
       id_winner
from lot as a
       left join bet b
                 on a.id_lot = b.id_lot
       inner join category c on a.id_category = c.id_category
group by a.name, start_price, image,data_create,end_date, c.name, id_winner
having ISNULL(end_date)
order by data_create desc;

SELECT `id_lot`,`category`.`name` FROM lot INNER JOIN category ON `lot`.`id_category` = `category`.`id_category`;
UPDATE `lot` SET `name`= 'Текст' WHERE `id_lot` = 1;   UPDATE `lot` SET `name`= '2014 Rossignol District Snowboard' WHERE `id_lot` = 1;
SELECT `id_bet`,`bet`.`id_user`,`date`,`sum` FROM `bet` INNER JOIN `user` ON `bet`.`id_user` = `user`.`id_user` WHERE DATE(`date`) = '2022-05-18';