INSERT INTO `lots` (`id_lot`, `id_winner`, `id_category`, `id_user`, `data_create`, `name`, `description`, `image`, `start_price`, `end_date`, `bet_step`) VALUES (NULL, '1', '1', '1', '2022-05-18 12:41:53', '2014 Rossignol District Snowboard', 'Легкий маневренный сноуборд, готовый дать жару в любом парке, растопив снег мощным щелчкоми четкими дугами. Стекловолокно Bi-Ax, уложенное в двух направлениях, наделяет этот снаряд отличной гибкостью и отзывчивостью, а симметричная геометрия в сочетании с классическим прогибом кэмбер позволит уверенно держать высокие скорости. А если к концу катального дня сил совсем не останется, просто посмотрите на Вашу доску и улыбнитесь, крутая графика от Шона Кливера еще никого не оставляла равнодушным.', 'img/lot-1.jpg', '10999', '2022-05-16 09:30:20', '1'),
